import React from "react";
import "../css/CreateQuizPage.css";

const CreateQuiz = () => {
  return (
    <div className="quiz-container">
      <h2>Create Quiz</h2>

      <div className="quiz-image-section">
        <img
          src="https://via.placeholder.com/100x100.png?text=Quiz"
          alt="Quiz"
          className="quiz-image"
        />
        <p>Quiz Image</p>
      </div>

      <form className="quiz-form">
        <label>Quiz Name *</label>
        <input type="text" placeholder="Enter quiz name" />

        <label>Quiz URL *</label>
        <input type="text" placeholder="Enter quiz URL" />

        <div className="form-row">
          <div>
            <label>Start Date *</label>
            <input type="date" />
          </div>
          <div>
            <label>Start Time *</label>
            <input type="time" />
          </div>
        </div>

        <div className="form-row">
          <div>
            <label>End Date *</label>
            <input type="date" />
          </div>
          <div>
            <label>End Time *</label>
            <input type="time" />
          </div>
        </div>

        <div className="form-row">
          <div>
            <label>Quiz Mode</label>
            <input type="text" placeholder="Mode" />
          </div>
          <div>
            <label>Quiz Type</label>
            <input type="text" placeholder="Public/Private" disabled />
          </div>
        </div>

        <label>Time Zone *</label>
        <input type="text" placeholder="Enter time zone" />

        <label>Quiz Description</label>
        <textarea
          rows="4"
          defaultValue={`Hey there!
We’re thrilled to have you join the quiz!
Remember, the faster you answer and the more questions you get right, the better your chances of climbing to the top of the leaderboard. So stay sharp, think fast, and give it your best shot—good luck!`}
        ></textarea>

